$(document).ready(function(){
    $(".one").hover(function(){
        $("#one").css("font-size", "44px");
        $("#one").css("-webkit-transform", "rotate(90deg)");
        $("#one").css("-moz-transform", "rotate(90deg)");
        $("#one").css("-o-transform", "rotate(90deg)");
        $("#one").css("-ms-transform", "rotate(90deg)");
        $("#one").css("transform", "rotate(90deg)");
        }, function(){
        $("#one").css("font-size", "36px");
        $("#one").css("-webkit-transform", "rotate(0)");
        $("#one").css("-moz-transform", "rotate(0)");
        $("#one").css("-o-transform", "rotate(0)");
        $("#one").css("-ms-transform", "rotate(0)");
        $("#one").css("transform", "rotate(0)");
    });
});
